catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
	var rowObj = [];
	rowObj.push({
		"input_name": 'size',
		"input_description": '',
		"input_type": 'string',
		"input_value": g_form.getValue('size')

	});
	rowObj.push({
		"input_name": 'location',
		"input_description": '',
		"input_type": 'string',
		"input_value": g_form.getValue('location')

	});
	g_form.setValue('inputs', JSON.stringify(rowObj));	

}
