sys_variable_value.value:
(function execute(inputs, outputs) {
	var connection_url = inputs.manager_url;
    var execution_id = JSON.parse(inputs.execution_id);
    var request = new sn_ws.RESTMessageV2();
  var encryptmypwd = new global.myEncrypterInclude(); //Note the global prefix
var creds = encryptmypwd.encryptPwd(inputs.credentials);
  	gs.info("creds "+creds);
  	request.setRequestHeader("Authorization","Basic "+inputs.credentials);
    request.setEndpoint(connection_url + 'api/v3.1/events');
    request.setQueryParameter("execution_id", execution_id.id);
  	request.setStringParameter("execution_id", execution_id.id);
  	request.setHttpMethod("GET");
    var response = request.execute();
    gs.info("response body "+response.getBody());
    gs.info("response code"+response.getStatusCode());
})(inputs, outputs);

