sys_variable_value.value:
(function execute(inputs, outputs) {
  var cfy_integration = new cloudify_integration();
	// convert inputs array object to dict
  var dep_inputs = {};
  for (var i = 0; i < inputs.dep_inputs.length; i++) {
    var variable = inputs.dep_inputs[i];
    if (variable.input_name!='' && variable.input_value!= ''){
      switch(variable.input_type){
        case 'integer':
          dep_inputs[variable.input_name] = parseInt(variable.input_value);
          break;
        case 'float':
          dep_inputs[variable.input_name] = parseFloat(variable.input_value);
          break;
        case 'boolean':
          dep_inputs[variable.input_name] = variable.input_value.toLowerCase()==='true';
          break;
        case 'list':
          var arrayVal = [];
          var jsonVal = JSON.parse(variable.input_value);
          for( var j = 0 ;j< jsonVal.length;j++)
            arrayVal.push(jsonVal[j]);
          dep_inputs[variable.input_name] = arrayVal;
          break;
        case 'dict':
          dep_inputs[variable.input_name] = JSON.parse(variable.input_value);
          break;
        default:
          dep_inputs[variable.input_name] = variable.input_value;
          break;
      }
    }
  }

  outputs.result = cfy_integration.updateDeployment(inputs.config_name, inputs.blueprint_id,inputs.deployment_id,dep_inputs);

})(inputs, outputs);

